import {useRouter} from 'next/router';


const NewsDetail = (props) => {
    const router = useRouter();
    
    console.log(router);
    

    return (

        <div>
            <h1>{router.query.title}</h1>
            <h5></h5>
        </div>
    )
}


NewsDetail.getInitialProps = (contex) => {
    const res = fetch(`https://newsapi.org/v2/everything?q=apple&from=2020-01-14&to=2020-01-14&sortBy=popularity&apiKey=21105020649e498283c05f6fa7ab0cb4`);
    const data = res.json();
    console.log(data);
    console.log(contex);
    

    return {
        news: data
    }
    
}

export default NewsDetail;
